JSR223PostProcessor.stringProp:
import org.apache.jmeter.protocol.http.control.*

//Get cookie manager
CookieManager cm = sampler.getCookieManager();

String jsonCart =  vars.get("code");

Cookie c = new Cookie("cart", "DEFAULT_"+jsonCart, "app-davidlopeseumlabdr-dipql6oy.srv.ravcloud.com", "/", false, 1557578515)
Cookie c2 = new Cookie("mycart", jsonCart, "app-davidlopeseumlabdr-dipql6oy.srv.ravcloud.com", "/", false, 1557578515)

cm.add(c);

log.info("******************** SETTING COOKIE: " + jsonCart)
log.info("******************** Cookie value: " + cm.get(1).getValue()+" Path:"+cm.get(1).getPath())
log.info("******************** JSESSION value: " + cm.get(0).getValue()+" Path:"+cm.get(0).getDomain())

